import React from 'react';

function InputWord({ word, setWord, onTranslate }) {
    return (
        <div>
            <input
                type="text"
                value={word}
                placeholder="Введіть слово"
                onChange={(e) => setWord(e.target.value)}
                onKeyDown={(e) => { if (e.key === 'Enter') onTranslate(); }}
                style={{ padding: '8px', width: '80%', marginRight: '8px' }}
            />
            <button onClick={onTranslate}>Перекласти</button>
        </div>
    );
}

export default InputWord;
