import React, { useState } from 'react';
import axios from 'axios';
import InputWord from './components/InputWord';
import TranslationResult from './components/TranslationResult';
import CookieConsent from 'react-cookie-consent';

const backendUrl = 'http://localhost:4000'; // Твій бекенд має слухати тут

function App() {
    const [word, setWord] = useState('');
    const [translation, setTranslation] = useState('');
    const [partOfSpeech, setPartOfSpeech] = useState('');
    const [error, setError] = useState('');

    const handleTranslate = async () => {
        setError('');
        setTranslation('');
        setPartOfSpeech('');
        if (!word.trim()) {
            setError('Введіть слово');
            return;
        }

        try {
            const [translateRes, posRes] = await Promise.all([
                axios.get(`${backendUrl}/api/translate`, { params: { word } }),
                axios.get(`${backendUrl}/api/partofspeech`, { params: { word } }),
            ]);
            setTranslation(translateRes.data.translation);
            setPartOfSpeech(posRes.data.partOfSpeech);
        } catch {
            setError('Слово не знайдено або помилка сервера');
        }
    };

    return (
        <div style={{ maxWidth: 500, margin: 'auto', padding: 20 }}>
            <h1>Simple Translator</h1>
            <InputWord word={word} setWord={setWord} onTranslate={handleTranslate} />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <TranslationResult translation={translation} partOfSpeech={partOfSpeech} />
            <CookieConsent
                location="bottom"
                buttonText="Прийняти"
                cookieName="simple-translator-cookie"
                style={{ background: "#2B373B" }}
                buttonStyle={{ color: "#4e503b", fontSize: "13px" }}
                expires={150}
            >
                Цей сайт використовує cookies для покращення користувацького досвіду та відповідності GDPR.
            </CookieConsent>
        </div>
    );
}

export default App;
